import React ,{useState} from "react";
import Note from "./note";



function CreateNote(props){

    var [newNote , setNewNotes] = useState({title: "" , content:"" });
    

    function newNote(event){
        var Name = event.target.name;
        var {title , content} = newNote ;
        if(Name === "title"){
            title = event.target.value ;
        }

        else if(Name ==="content"){
            content = event.target.value;
        }

        setNewNotes({title:title , content:content});
        
    }

    function newSubmission(event){
        
        event.preventDefault();
        props.addNote(newNote);
        
    
    
    
    
    }
    
    

    return(
        <div>
        <form>
        <input placeholder ="title" name = "title" onChange = {newNote}/>
        <textarea placeholder = "Take a note ..." name = "content" onChange = {newNote} />
        <button type="submit" onClick = {newSubmission}>add</button>
        </form>
        </div>
    );

}

export default CreateNote ;
