import React ,{useState} from "react";
import Note from "./note";



function CreateNote(props){

    var [newNotes , setNewNotes] = useState({title: "" , content:"" });
    var [inputArea , setInputArea] = useState(false);

    function newNote(event){
        var Name = event.target.name;
        var Value = event.target.value;

        setNewNotes((prevNote) => {
           return {...prevNote , [Name]: Value}
        });
        
    }

    function newSubmission(event){
        event.preventDefault();
        props.addNote(newNotes);
        setNewNotes({title: "" , content:"" });
    
    
    }
    function formInputArea(){
        setInputArea(true);
        }
    
    

    return(
        <div onClick = {formInputArea}>
        <form onSubmit = {newSubmission}>
        {inputArea && <input placeholder ="Title" name = "title" onChange = {newNote} value = {newNotes.title} spellCheck="false" /> };
        <textarea placeholder = "Add a note" name = "content" onChange = {newNote} value ={newNotes.content} spellCheck = "false"/>
        <button type="submit" >add</button>
        </form>
        </div>
    );

}

export default CreateNote ;
